import "./MusicUploadForm.css";
import TrackNum from "../trackNum/TrackNum";
import TrackSearch from "../trackSearch/TrackSearch";
import { useState } from "react";
import SongsData from '/../data/SongData'; 
import SongList from "../songList/SongList";




const MusicUploadForm = () => {


    const [filteredSongs, setFilteredSongs] = useState(songsData); // Initialize filteredSongs with all songs

    // Function to handle filtering based on search query
    const handleFilterChange = (query) => {
        const filtered = songsData.filter(song =>
            song.songName.toLowerCase().includes(query.toLowerCase())
        );
        setFilteredSongs(filtered);
    };

    

    return (
           <div className="upl-form">
                <TrackNum/>
                <TrackSearch  onFilterChange={handleFilterChange} />
                <SongList songs={filteredSongs} />
           </div>
    )
}

export default MusicUploadForm;
